$(document).ready(function(){$(".toggle-selected").click(function(){$(".toggle-selected").not(this).removeClass("selected");$(this).toggleClass("selected")});$(".btn").click(function(){var a=$("li.selected").attr("data-value");$.ajax({url:"webservice.php/faculty/"+a,type:"GET",dataType:"json",contentType:"application/json",cache:!1}).done(function(a){!0==a.result?window.location="/documents.php":alert("Unable to load documents for the selected faculty member.")})})});